sys_ws_operation.operation_script:
function deleteAllRecords(tableName){
var record=new GlideRecordSecure(tableName);
		record.query();
		var cnt=0;
		while (record.next()){
			cnt++;
			record.deleteRecord();
		}
	return cnt;
}

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var cnts=[];
    cnts.push(deleteAllRecords('x_195638_cisc475_s_group'));
	cnts.push(deleteAllRecords('x_195638_cisc475_s_scores'));
	cnts.push(deleteAllRecords('x_195638_cisc475_s_students'));
	for (var i=0;i<10;i++){
		var gp=new GlideRecordSecure('x_195638_cisc475_s_group');
		gp.initialize();
		gp.group_name="Test "+(i+1);
		gp.insert();

	}
	//insert student records, first you
	var groups=[];
	var rec=new GlideRecordSecure('x_195638_cisc475_s_group');	
	rec.query();
	while (rec.next())
		groups.push(rec.getValue('sys_id'));
	rec=new GlideRecordSecure('sys_user');
	rec.query();
	var cnt=0;
	while (rec.next() && cnt<50){
		var user=new GlideRecordSecure('x_195638_cisc475_s_students');
		user.initialize();
		user.user=rec.sys_id;
		user.sprintgroup=groups[Math.floor(cnt/5)];
		user.registered=true;
		user.insert();		
		cnt++;
	}
	var me=new GlideRecordSecure('x_195638_cisc475_s_students');
	me.initialize();
	me.user=gs.getUserID();
	me.sprintgroup=groups[0];
	me.registered=true;
	me.insert();
	response.setBody({ok: true});

})(request, response);
